import { defineMessages, InjectedIntl } from 'react-intl'
import * as pdfMake from 'pdfmake/build/pdfmake'
import pdfFonts from 'src/vfs_fonts'
import {
  CERTIFICATE_MONEY_RECEIPT_DATE_FORMAT,
  CERTIFICATE_DATE_FORMAT
} from 'src/utils/constants'
import * as moment from 'moment'
import 'moment/locale/bn'
import 'moment/locale/en-ie'
import { Event } from 'src/forms'

const SEAL_BD_GOVT = `data:image/png;base64,`
const secreatarySignature = `data:image/png;base64,`
const chairSignature = `data:image/png;base64,`

const messages = defineMessages({
  certificateHeader: {
    id: 'register.work-queue.certificate.header'
  },
  certificateSubHeader: {
    id: 'register.work-queue.certificate.subheader'
  },
  certificateIssuer: {
    id: 'register.work-queue.certificate.issuer'
  },
  certificatePaidAmount: {
    id: 'register.work-queue.certificate.amount'
  },
  certificateService: {
    id: 'register.work-queue.certificate.service'
  }
})

export type MultiLingualDetails = {
  bn: string
  en: string
}

export type CertificateDetails = {
  registrationNo: string
  name: MultiLingualDetails
  doe: MultiLingualDetails
  registrationLocation: MultiLingualDetails
  eventLocation: MultiLingualDetails
  printDate?: MultiLingualDetails
  event: string
} | null

export type Registrant = {
  name: string
  DOBDiff: string
}

export type Issuer = {
  name: string
  role: string
  issuedAt: string
}

const certificateDefinitionText = {
  [Event.BIRTH]: {
    REGIATRATION_NO: 'Birth Registration No',
    OCCURANCE: {
      en: 'Was born on',
      bn: 'জন্মগ্রহণ করেন'
    }
  },
  [Event.DEATH]: {
    REGIATRATION_NO: 'Death Registration No',
    OCCURANCE: {
      en: 'Died on',
      bn: 'মারা যান'
    }
  }
}

export function generateMoneyReceipt(
  intl: InjectedIntl,
  registrant: Registrant,
  IssuerDetails: Issuer,
  amount: string,
  language: string
) {
  moment.lang(language)
  const dateOfPayment = moment().format(CERTIFICATE_MONEY_RECEIPT_DATE_FORMAT)
  const docDefinition = {
    info: {
      title: 'Receipt-for-Birth-Certificate'
    },
    defaultStyle: {
      font: 'notosans'
    },
    content: [
      {
        text: intl.formatMessage(messages.certificateHeader),
        style: 'header'
      },
      {
        text: registrant.name,
        style: 'header'
      },
      '\n\n',
      {
        text: [
          {
            text: intl.formatMessage(messages.certificateService)
          },
          {
            text: intl.formatMessage(messages.certificateSubHeader, {
              DOBDiff: registrant.DOBDiff
            }),
            style: 'subheader'
          }
        ]
      },
      intl.formatMessage(messages.certificatePaidAmount),
      {
        text: `${amount}\n\n`,
        style: 'amount'
      },
      intl.formatMessage(messages.certificateIssuer, {
        issuedAt: IssuerDetails.issuedAt,
        role: IssuerDetails.role,
        name: IssuerDetails.name,
        dateOfPayment
      })
    ],
    styles: {
      header: {
        fontSize: 18
      },
      amount: {
        font: 'notosanscurrency',
        fontSize: 30,
        bold: true
      },
      subheader: {
        bold: true
      }
    }
  }
  const fonts = {
    bn: {
      notosans: {
        normal: 'NotoSansBengali-Light.ttf',
        regular: 'NotoSansBengali-Light.ttf',
        bold: 'NotoSansBengali-Regular.ttf'
      },
      notosanscurrency: {
        normal: 'NotoSansBengali-Light.ttf',
        regular: 'NotoSansBengali-Light.ttf',
        bold: 'NotoSansBengali-Light.ttf'
      }
    },
    en: {
      notosanscurrency: {
        normal: 'NotoSansBengali-Light.ttf',
        regular: 'NotoSansBengali-Light.ttf',
        bold: 'NotoSansBengali-Light.ttf'
      },
      notosans: {
        normal: 'NotoSans-Light.ttf',
        regular: 'NotoSans-Light.ttf',
        bold: 'NotoSans-Regular.ttf'
      }
    }
  }
  const font = fonts[language]

  pdfMake.vfs = pdfFonts.pdfMake.vfs
  const generatedPDF = pdfMake.createPdf(docDefinition, null, font)

  generatedPDF.print()
}

function getCertificateObject(certificateDetails: CertificateDetails) {
  moment.locale('en')
  const dateEn = moment().format(CERTIFICATE_DATE_FORMAT)
  moment.locale('bn')
  const dateBn = moment().format(CERTIFICATE_DATE_FORMAT)
  const NOT_FOUND = 'Not Found'
  const EVENT = certificateDetails ? certificateDetails.event : Event.BIRTH
  const certificateDynamicText = certificateDefinitionText[EVENT]

  const data: CertificateDetails = {
    registrationNo: NOT_FOUND,
    name: {
      bn: NOT_FOUND,
      en: NOT_FOUND
    },
    doe: {
      bn: NOT_FOUND,
      en: NOT_FOUND
    },
    registrationLocation: {
      bn: NOT_FOUND,
      en: NOT_FOUND
    },
    eventLocation: {
      bn: NOT_FOUND,
      en: NOT_FOUND
    },
    event: NOT_FOUND,
    ...certificateDetails
  }

  data.printDate = {
    bn: dateBn,
    en: dateEn
  }

  let nameRow
  let eventLocationRow
  if (data.name.en) {
    nameRow = [
      {
        margin: [0, 39, 0, 0],
        columns: [
          {
            text: 'This is to certify that',
            alignment: 'right'
          },
          {
            text: data.name.en,
            bold: true
          }
        ]
      },
      {
        font: 'notosansbn',
        columns: [
          {
            text: 'এই যে প্রত্যয়িত করা হয়',
            alignment: 'right'
          },
          {
            text: data.name.bn,
            bold: true
          }
        ]
      }
    ]
  } else {
    nameRow = [
      {
        margin: [0, 39, 0, 0],
        font: 'notosansbn',
        columns: [
          {
            text: 'এই যে প্রত্যয়িত করা হয়',
            alignment: 'right'
          },
          {
            text: data.name.bn,
            bold: true
          }
        ]
      }
    ]
  }

  if (data.eventLocation.en) {
    eventLocationRow = [
      {
        margin: [0, 39, 0, 0],
        columns: [
          {
            text: 'Place of birth',
            alignment: 'right'
          },
          {
            text: data.eventLocation.en,
            bold: true
          }
        ]
      },
      {
        font: 'notosansbn',
        columns: [
          {
            text: 'জন্মগ্রহণ করেন',
            alignment: 'right'
          },
          {
            text: data.eventLocation.bn,
            bold: true
          }
        ]
      }
    ]
  } else {
    eventLocationRow = [
      {
        margin: [0, 39, 0, 0],
        columns: [
          {
            text: '',
            alignment: 'right'
          },
          {
            text: '',
            bold: true
          }
        ]
      }
    ]
  }

  const certificateDefinition = {
    defaultStyle: {
      font: 'notosans',
      columnGap: 20
    },
    background: () => {
      return {
        canvas: [
          {
            type: 'rect',
            x: 0,
            y: 0,
            w: 595.28,
            h: 841.89,
            color: '#FFF9F0'
          }
        ]
      }
    },
    content: [
      {
        image: SEAL_BD_GOVT,
        fit: [78, 78],
        alignment: 'center',
        margin: [0, 58, 0, 23]
      },
      {
        text: [
          certificateDynamicText.REGIATRATION_NO,
          '\n',
          data.registrationNo
        ],
        style: {
          alignment: 'center',
          fontSize: 18
        }
      },
      nameRow,
      [
        {
          margin: [0, 39, 0, 0],
          columns: [
            {
              text: certificateDynamicText.OCCURANCE.en,
              alignment: 'right'
            },
            {
              text: data.doe.en,
              bold: true
            }
          ]
        },
        {
          font: 'notosansbn',
          columns: [
            {
              text: certificateDynamicText.OCCURANCE.bn,
              alignment: 'right'
            },
            {
              text: data.doe.bn,
              bold: true
            }
          ]
        }
      ],
      eventLocationRow,
      [
        {
          text: `This event was registered at ${data.registrationLocation.en}`,
          alignment: 'center',
          margin: [0, 50, 0, 0]
        },
        {
          text: `${data.registrationLocation.bn} এই ঘটনাটি নিবন্ধিত হয়েছিল।`,
          font: 'notosansbn',
          alignment: 'center'
        }
      ],
      {
        columns: [
          [
            {
              image: secreatarySignature,
              alignment: 'center',
              margin: [0, 30, 0, 0]
            },
            { alignment: 'center', text: 'Secretary: Md. Jahangir Alam' },
            {
              alignment: 'center',
              text: 'মোঃ জাহাঙ্গির আলম',
              font: 'notosansbn'
            }
          ],
          [
            {
              image: chairSignature,
              alignment: 'center',
              margin: [0, 30, 0, 0]
            },
            { alignment: 'center', text: 'Chair: A.K.M Mahmudur Rohman' },
            {
              alignment: 'center',
              text: 'এ কে এম মাহমুদুর রহমান',
              font: 'notosansbn'
            }
          ]
        ]
      },
      [
        {
          text: data.printDate.en,
          bold: true,
          alignment: 'center',
          margin: [0, 50, 0, 0]
        },
        {
          text: data.printDate.bn,
          font: 'notosansbn',
          bold: true,
          alignment: 'center'
        },
        {
          text: 'Date of issue',
          alignment: 'center',
          margin: [0, 10, 0, 0]
        },
        {
          text: 'প্রদান এর তারিখ',
          alignment: 'center',
          font: 'notosansbn'
        }
      ]
    ],
    styles: {}
  }
  const fonts = {
    notosans: {
      normal: 'NotoSans-Light.ttf',
      regular: 'NotoSans-Light.ttf',
      bold: 'NotoSans-Regular.ttf'
    },
    notosansbn: {
      normal: 'NotoSansBengali-Light.ttf',
      regular: 'NotoSansBengali-Light.ttf',
      bold: 'NotoSansBengali-Regular.ttf'
    }
  }

  pdfMake.vfs = pdfFonts.pdfMake.vfs
  return pdfMake.createPdf(certificateDefinition, null, fonts)
}

export async function generateCertificateDataURL(
  certificateDetails: CertificateDetails,
  callBack: (pdf: string) => void
) {
  await getCertificateObject(certificateDetails).getDataUrl((pdf: string) => {
    callBack(pdf)
  })
}

export function generateAndPrintCertificate(
  certificateDetails: CertificateDetails
) {
  getCertificateObject(certificateDetails).print()
}
